mapper.select:
SELECT UT.OBJECT_NAME AS TABLE_NAME, NVL(SUBSTRB(UTC.COMMENTS,1,500),'') AS TABLE_COMMENT, UT.OWNER AS SCHEMA_NAME FROM ALL_OBJECTS UT, ALL_TAB_COMMENTS UTC WHERE UT.OBJECT_NAME = UTC.TABLE_NAME AND UT.OWNER = UTC.OWNER --EAXM AND UT.OBJECT_TYPE IN ('TABLE','VIEW','MATERIALIZED VIEW') AND UT.OBJECT_TYPE IN ('TABLE') AND UT.OWNER =#{TABLE_SCHEMA}
AND UT.OBJECT_NAME IN ( ${TABLE_LIST} )
AND UT.OBJECT_NAME NOT LIKE 'BIN$%' AND UT.OWNER NOT IN ('SCOTT', 'OWBSYS_AUDIT', 'OWBSYS', 'APEX_030200', 'FLOWS_FILES', 'APEX_PUBLIC_USER', 'MGMT_VIEW', 'SYSMAN', 'SPATIAL_CSW_ADMIN_USR', 'SPATIAL_WFS_ADMIN_USR', 'MDDATA', 'OLAPSYS', 'ORDSYS', 'ORDDATA', 'ORDPLUGINS', 'MDSYS', 'SI_INFORMTN_SCHEMA', 'XS$NULL', 'ANONYMOUS', 'XDB', 'CTXSYS', 'EXFSYS', 'WMSYS', 'APPQOSSYS', 'DBSNMP', 'ORACLE_OCM', 'DIP', 'OUTLN', 'SYSTEM', 'SYS', --EAXM 'PM', 'BI', 'IX', 'OE', 'HR', 'SH') ORDER BY SCHEMA_NAME, TABLE_NAME

SELECT LOWER(CON.CONSTRAINT_SCHEMA) AS constraint_schema , LOWER(CON.CONSTRAINT_NAME) AS constraint_name , LOWER(CON.TABLE_SCHEMA) AS table_schema , LOWER(CON.TABLE_NAME) AS table_name ,CASE WHEN con.constraint_type='PRIMARY KEY' THEN 'P' WHEN con.constraint_type='FOREIGN KEY' THEN 'F' WHEN con.constraint_type='UNIQUE' THEN 'U' ELSE NULL END AS constraint_type , LOWER(CU.COLUMN_NAME) AS column_name , LOWER(CU.ORDINAL_POSITION) AS ordinal_position ,NULL AS ref_table_schema ,NULL AS ref_table_name ,NULL AS ref_column_name ,NULL AS match_option ,NULL AS update_rule ,NULL AS delete_rule FROM information_schema.table_constraints CON INNER JOIN information_schema.key_column_usage AS CU ON CON.constraint_name = CU.constraint_name WHERE CON.TABLE_SCHEMA = #{TABLE_SCHEMA} AND CON.TABLE_NAME = #{TABLE_NAME} AND CON.CONSTRAINT_TYPE IN ('PRIMARY KEY', 'UNIQUE')
UNION ALL
SELECT LOWER(TC.CONSTRAINT_SCHEMA) AS constraint_schema , LOWER(TC.CONSTRAINT_NAME) AS constraint_name , LOWER(TC.TABLE_SCHEMA) AS table_schema , LOWER(TC.TABLE_NAME) AS table_name ,CASE WHEN TC.constraint_type='PRIMARY KEY' THEN 'P' WHEN TC.constraint_type='FOREIGN KEY' THEN 'F' WHEN TC.constraint_type='UNIQUE' THEN 'U' ELSE NULL END AS constraint_type , LOWER(CU.COLUMN_NAME) AS column_name , LOWER(CU.ORDINAL_POSITION) AS ordinal_position , LOWER(RC.UNIQUE_CONSTRAINT_SCHEMA) AS ref_table_schema , LOWER(RC.UNIQUE_CONSTRAINT_NAME) AS ref_table_name , LOWER(CU.COLUMN_NAME) AS ref_column_name , LOWER(RC.MATCH_OPTION) AS match_option , LOWER(RC.UPDATE_RULE) AS update_rule , LOWER(RC.DELETE_RULE) AS delete_rule FROM information_schema.referential_constraints AS RC INNER JOIN information_schema.key_column_usage AS CU ON RC.constraint_name = CU.constraint_name INNER JOIN information_schema.table_constraints AS TC ON RC.constraint_name = TC.constraint_name WHERE TC.TABLE_SCHEMA = #{TABLE_SCHEMA} AND TC.TABLE_NAME = #{TABLE_NAME}

SELECT table_name FROM information_schema.tables WHERE table_schema=#{TABLE_SCHEMA}
AND table_type IN ('BASE TABLE')
AND table_type IN ('BASE TABLE','VIEW')
